package edu.orangecoastcollege.view;

import edu.orangecoastcollege.controller.Controller;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.scene.control.Button;

public class CountryCityScene {

	// Event Listener on Button.onAction
	@FXML
	public void backButton(ActionEvent event) {
		// TODO Autogenerated
	       ViewNavigator.loadScene("User Information", ViewNavigator.USER_INFORMATION_SCENE);
	       Controller.countryChoosen=0;
	}
	
    @FXML
    public void goToChooseScene(ActionEvent event)
    {
        Object object = event.getSource();
        if (object instanceof Button)
        {
            Button b = (Button) object;
            String name = b.getId();
           if(name!=null){
            switch(name)
            {
                case "USA":
                    Controller.countryChoosen= Integer.valueOf(Controller.USA_COUNTRY_CODE);
                    break;
                case"UK":
                    Controller.countryChoosen= Integer.valueOf(Controller.UK_COUNTRY_CODE);
               break;
                case"SPAIN":
                    Controller.countryChoosen= Integer.valueOf(Controller.SPAIN_COUNTRY_CODE);
                    break;
                case "JAPAN":
                    Controller.countryChoosen= Integer.valueOf(Controller.JAPAN_COUNTRY_CODE);
                    break;
                case "BRAZIL":
                    Controller.countryChoosen= Integer.valueOf(Controller.BRAZIL_COUNTRY_CODE);
                    break;
                case "VIETNAM":
                    Controller.countryChoosen= Integer.valueOf(Controller.VIETNAM_COUNTRY_CODE);
                    break;
               
            }
           }
           System.out.println(Controller.countryChoosen);
        }
        ViewNavigator.loadScene("User Info", ViewNavigator.CHOOSE_SCENE);
     //Event e = new Event();

    }

}
